mapper.select:
select

from grain_article_award
where 1 = 1

    and employee_id = #{employeeId}


    and employee_name = #{employeeName}


    and award_grain_value = #{awardGrainValue}


    and award_grain_time = #{awardGrainTime}


    and article_id = #{articleId}


    and created = #{created}


    and updated = #{updated}

LIMIT #{page}, #{pageSize}

select
count(*)
from grain_article_award
where 1 = 1

    and employee_id = #{employeeId}


    and employee_name = #{employeeName}


    and award_grain_value = #{awardGrainValue}


    and award_grain_time = #{awardGrainTime}


    and article_id = #{articleId}


    and created = #{created}


    and updated = #{updated}

select
sum(award_grain_value)
from grain_article_award
where 1 = 1

    and employee_id = #{employeeId}


    and employee_name = #{employeeName}


    and award_grain_value = #{awardGrainValue}


    and award_grain_time = #{awardGrainTime}


    and article_id = #{articleId}


    and created = #{created}


    and updated = #{updated}
