mapper.select:
select

from grain_info
where 1 = 1

    and enterpr_id = #{enterprId}


    and enterpr_name = #{enterprName}


    and status = #{status}


    and cumulation_read_quantity = #{cumulationReadQuantity}


    and left_grain_value = #{leftGrainValue}


    and typeList = #{typeList}


    and apply_time = #{applyTime}


    and established_time = #{establishedTime}


    and service_status = #{serviceStatus}


    and created = #{created}


    and updated = #{updated}

LIMIT #{page}, #{pageSize}
