mapper.select:
select

from grain_opt_info
where 1 = 1

    and opt_id = #{optId}


    and parent_opt_id = #{parentOptId}


    and grain_id = #{grainId}


    and enterpr_id = #{enterprId}


    and enterpr_name = #{enterprName}


    and receive_time = #{receiveTime}


    and receive_credit_value = #{receiveCreditValue}


    and created = #{created}


    and updated = #{updated}

order by receive_time desc
LIMIT #{page}, #{pageSize}

select
count(*)
from grain_opt_info
where 1 = 1

    and opt_id = #{optId}


    and parent_opt_id = #{parentOptId}


    and grain_id = #{grainId}


    and enterpr_id = #{enterprId}


    and enterpr_name = #{enterprName}


    and receive_time = #{receiveTime}


    and receive_credit_value = #{receiveCreditValue}


    and created = #{created}


    and updated = #{updated}
