mapper.select:
select

from grain_special_person
where 1 = 1

    and employee_id = #{employeeId}


    and employee_name = #{employeeName}


    and employee_no = #{employeeNo}


    and single_award_grain_value = #{singleAwardGrainValue}


    and enterpr_id = #{enterprId}


    and created = #{created}


    and updated = #{updated}

LIMIT #{page}, #{pageSize}
