mapper.select:
  select distinct id,menu_code menuCode,menu_name menuName,menu_level menuLevel,menu_parent menuParent,
menu_url menuUrl,menu_order menuOrder from sys_menu
where id in(select menu_id from sys_role_menu where role_id in (select id from sys_role where id in(select role_id from sys_user_role where user_id = #{userId}))) and menu_parent = #{menuParent}
