mapper.select:
select

  distinct


from usr_user_info




  order by ${orderByClause}


  LIMIT #{page}, #{pageSize}

select

from usr_user_info
where 1 = 1

  and login_name = #{login_name}


  and user_name = #{user_name}


  and department_id = #{department_id}


  and mobile = #{mobile}


  and email = #{email}


  and status = #{status}

  LIMIT #{page}, #{pageSize}

select
count(1)
from usr_user_info
where 1 = 1

  and login_name = #{login_name}


  and user_name = #{user_name}


  and department_id = #{department_id}


  and mobile = #{mobile}


  and email = #{email}


  and status = #{status}
